import React, { useEffect, useState } from "react";
import "./style.css";
import Button from '../Button'
import wakeButtons from "../wakeButtons";
import { Link } from "react-router-dom";
import userService from "../../services/userService"

export default function Board() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    wakeButtons("15%", 20, "65%", 75);

    userService.getAll().then((users) => {
      // Filter users with a non-zero score in the "men" category
      const filteredUsers = users.filter(
        (user) => user.categories.men.score > 0
      );

      // Sort the filtered users by their "men" category score in descending order
      const sortedUsers = filteredUsers.sort(
        (a, b) => b.categories.men.score - a.categories.men.score
      );

      // Set the state with the sorted users
      setUsers(sortedUsers);
    });
  }, []);

  return (
    <>
      <div className="bc">
        <h1 className="header" id="text">
          LEADERBOARD - MEN:
        </h1>
        <ul>
          <li>
            <Link to="/">
              <Button name={"BACK"} />
            </Link>
          </li>
        </ul>
        <div className="leaderboard">
          {users.map((user, index) => (
            <div key={user.id}>
              <p className="leaderboard-user">
                {index + 1}. {user.username} - {user.categories.men.score} points
              </p>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
