import React, { useEffect, useState } from "react";
import "./style.css";
import Button from "../Button";
import { Link } from "react-router-dom"
import wakeButtons from "../wakeButtons";
import { runTimer, nameCheck, Session } from "../menNamesGame";
import userService from "../../services/userService"
import categoryService from "../../services/categoryService"

export default function Game() {
  const [game, setGame] = useState({});

  useEffect(() => {
    wakeButtons("10%", 30, "40%", 80, 30);
    setGame(new Session());
  }, []);

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      nameCheck(game);
      runTimer(game);
    }
  };

  const handleBackToMenu = () => {
    // Stop the timer and perform necessary actions
    game.run = false;
    document.getElementById('user').disabled = true;
    // Perform any additional actions required when going back to the menu

    // Update the active user and category
    userService.updateActive(game.playerId, game.points, game.catName, false);
    categoryService.updateActive(game.catId, false);
  };

  return (
    <>
      <div className="bc">
        <h1 className="header" id="text">
          NORMAL
        </h1>
        <h2 className="points">POINTS</h2>
        <h3 className="time">TIME</h3>
        <p id="timer">0:07</p>
        <p id="points">0</p>
        <ul>
          <li id="help">Give name and hit enter!</li>
          <li>
            <Link to="/">
              <Button name={"BACK TO MENU"} onClick={handleBackToMenu} />
            </Link>
          </li>
          <li>
            <form id="name">
              <input
                type="text"
                id="user"
                name="user"
                autoComplete="off"
                onKeyDown={handleKeyPress}
              ></input>
            </form>
          </li>
        </ul>
      </div>
    </>
  );
}
